import React, { useState, useEffect } from "react";
import './Options.css'

const StocksOptions = (props) => {
    const { setState, actionProvider } = props;
    const [ priceStock, setPriceStock ] = useState("") 

  const options = [
    {
      name: "AAPL",
      id: 1,
    },
    {
      name: "NVDA",
      id: 2,
    },
    {
      name: "MSFT",
      id: 3,
    },
  ];

  const setIDStock = (e) => {
    setState((state) => ({
      ...state,
      idStock: e.target.value,
      firtsSearch: true,
    }));

    actionProvider.handleSearchFinish(props);
  };

  // return <Options options={options} {...props} />;

  return (
    <div className="options">
        {options.map(option => {
          return (
            <button
              value={option.name}
              className="option"
              onClick={setIDStock}
              key={option.id}
            >
              {option.name}
            </button>
          );
        })}
    </div>
  )
};

export default StocksOptions;
