import React, { useContext } from "react";

import "./App.css";
import Price from "../Componets/Price";
import ListHistory from "../Componets/ListHistory";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";

import Chatbot from "react-chatbot-kit";
import "./App.css";

import ActionProvider from "../ActionProvider";
import MessageParser from "../MessageParser";
import config from "../Config";

import { StateContext } from "../StateContext.jsx";

const App = () => {
  const { state, setState } = useContext(StateProvider);

  const saveMessage = (message) => {
    setState((prevState) => ({
      ...prevState,
      messages: [...(prevState.messages || []), message],
    }));
  };

  const actionProvider = new ActionProvider((action) => {
    saveMessage(action.message);
    // Puedes hacer otras acciones aquí
  });

  return (
    <StateProvider>
      <>
        <Container>
          {/* Stack the columns on mobile by making one full-width and the other half-width */}
          <Row>
            <Col xs={12} md={8}>
              <Row>
                <Price />
                <ListHistory />
              </Row>
            </Col>
            <Col xs={6} md={4}>
              <Chatbot
                config={config}
                actionProvider={actionProvider}
                messageParser={MessageParser}
              />
            </Col>
          </Row>
        </Container>
      </>
    </StateProvider>
  );
};

export default App;
