import axios from "axios";

// Configuración global de Axios
axios.defaults.headers.common["Access-Control-Allow-Origin"] = "*";
axios.defaults.headers.common["Access-Control-Allow-Methods"] =
  "GET, POST, PUT";
axios.defaults.headers.common["Access-Control-Allow-Headers"] =
  "Origin, Content-Type, X-Auth-Token";

const BASE_URL = "http://localhost:8000";

const getListPriceStock = async (idStock) => {
  let data = await axios({
    method: "get",
    url: '/stocks',
    baseURL: BASE_URL
  });
  return data;
};

const getPriceCurrent = async (idStock) => {
  let data = await axios({
    method: "get",
    url: BASE_URL,
  });
  return data;
};

const postPriceCurrent = async (newPrice) => {
  let data = await axios({
    method: "post",
    url: BASE_URL,
    data: newPrice,
  });
  return data;
};

export default { getListPriceStock, getPriceCurrent, postPriceCurrent };
