// in config.js
import React, {useContext} from 'react';
import { createChatBotMessage } from 'react-chatbot-kit';
import StocksOptions from '../widgets/StocksOptions'

import { DataContext } from "../../App/index.jsx";


const botName = 'Fukencio';
const { state, setState } = useContext(DataContext);

const config = {

  initialMessages: [
    createChatBotMessage(
      `Hola mi nombre es ${botName}.`
    ),
    createChatBotMessage(
      "Estoy aqui para ayudarte a obtener los precios de las empresas en la bolsa de valores, por ejemplo:",
      {
        withAvatar: false,
        delay: 800,
        widget: 'Examples',
      }
    ),
  ],
  botName: botName,
  customStyles: {
    botMessageBox: {
      backgroundColor: '#4890e7',
    },
    chatButton: {
      backgroundColor: '#3177cb',
    },
  },
widgets: [
  {
    widgetName: 'Examples',
    widgetFunc: (props) => <StocksOptions {...props} />,
  },
]

};

export default config;
