import getPriceStock from '../Services'

class ActionProvider {
  constructor(createChatBotMessage, setStateFunc) {
    this.createChatBotMessage = createChatBotMessage;
    this.setState = setStateFunc;
  }

  handleSearchFinish = async ({ state }) => {
    const botMessage = this.createChatBotMessage("Listo", {
      loading: true,
      terminateLoading: true,
      withAvatar: true,
    });
    //setState({ type: addMessageToState, botMessage })
    const { data } = await getPriceStock(state.idStock);
  };

  handleInputStock = () => {
    const botMessage = this.createChatBotMessage(
      "Ingresa la clave de la accion a buscar:",
      {
        loading: true,
        terminateLoading: true,
        withAvatar: true,
      }
    );
    // setState({ type: addMessageToState})
  };
}

export default ActionProvider;
