import { actions } from "./actions";

export const reducer = (state, action) => {
  switch (action.type) {
    case actions.setReload:
      return {
        ...state,
        reload: !state.reload,
        firtsSearch: true,
      };
    case actions.addMessageToState:
      return {
        ...state,
        messages: [...state.messages, action.botMessage],
      };
    case actions.addPriceStockList:
      return {
        ...state,
        priceStockList: action.data,
      };
    default:
      return state;
  }
};
