import { actions } from "./actions";

export const reducer = (state, action) => {
  switch (action.type) {
    case actions.setReload:
      return {
        ...state,
        reload: !state.reload,
      };
    case actions.fetchStocks:
      return {
        ...state,
        searching: true,
        error: null,
      };
    case actions.fetchStocksError:
      return {
        ...state,
        searching: false,
        priceStockList: [],
        error: action.payload,
      };

    case actions.fetchStocksSuccess:
      return {
        ...state,
        searching: false,
        priceStockList: action.payload,
        error: null,
      };
    default:
      return state;
  }
};
