import React from 'react';
import getPriceStock from '../Services'
import GET_STOCKS from '../Services/queries';
import { useQuery } from '@apollo/client';

const ActionProvider = ({ createChatBotMessage, setState, children }) => {


  const handleSearchFinish = ({state}) => {
    const botMessage = createChatBotMessage(
      "Listo",
      {
        loading: true,
        terminateLoading: true,
        withAvatar: true,
      }
    );
    addMessageToState(botMessage)
    const { loading, error, data } = useQuery(GET_STOCKS);

    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error :(</p>;


  };

  const handleInputStock = () => {
    const botMessage = createChatBotMessage(
      "Ingresa la clave de la accion a buscar:",
      {
        loading: true,
        terminateLoading: true,
        withAvatar: true,
      }
    );
    addMessageToState(botMessage)
  };

  const handleSearchStock = () => {

  }

  const handleCustomWidget = (event) => {
    if (event && event.preventDefault) {
      event.preventDefault(); // Prevenir el comportamiento predeterminado del evento
    }
  }

  const addMessageToState = (botMessage) => {
    setState((prev) => ({
      ...prev,
      messages: [...prev.messages, botMessage],
    }));
  };

  // Put the handleHello and handleDog function in the actions object to pass to the MessageParser
  return (
    <div>
      {React.Children.map(children, (child) => {
        return React.cloneElement(child, {
          actions: {
            handleInputStock,
            handleSearchStock,
            handleCustomWidget,
            handleSearchFinish,
          },
        });
      })}
    </div>
  );
};

export default ActionProvider;
