import React from "react";

import Col from "react-bootstrap/Col";
import Raw from "react-bootstrap/Row";
import Container from "react-bootstrap/Container";

import Badge from "react-bootstrap/Badge";
import ListGroup from "react-bootstrap/ListGroup";

import "./styles.css";

import {convertDatetime, formatCurrency} from "../../Utils";

const Price = ({ state }) => {
  const { listPriceStock, idStock, currentPrice } = state;

  return (
    <Container>
      <Raw>
        <a>{currentPrice.symbol}</a>
        <Col xs={6} md={4}>
          Precio actual:
          <p>{formatCurrency(currentPrice.last)}</p>
        </Col>
        <Col xs={6} md={4}>
          Cierre anterior:
          <p>{formatCurrency(currentPrice.high)}</p>
        </Col>
        <Col xs={6} md={4}>
          Variacion:
          <p>{formatCurrency(currentPrice.low)}</p>
        </Col>
      </Raw>

      <Raw>
        <ListGroup as="ol">
          {listPriceStock.map((item) => (
            <ListGroup.Item
              as="li"
              className="d-flex justify-content-between align-items-start"
              key={item.id}
            >
              <div className="ms-2 me-auto">
                <div className="fw-bold">{formatCurrency(item.last)}</div>
                {item.volumen}
              </div>
              <Badge bg="primary" pill>
                {convertDatetime(item.date)}
              </Badge>
            </ListGroup.Item>
          ))}
        </ListGroup>
      </Raw>
    </Container>
  );
};

export default Price;
