import getPriceStock from "../Services";

class ActionProvider {
  constructor(createChatbotMessage, setStateFunc) {
    this.createChatbotMessage = createChatbotMessage;
    this.setState = setStateFunc;
  }

  handleSearchFinish = async () => {
    const fetchedData = async() => {
      const { data } = await getPriceStock("")
      console.log(data)
      return data;
    };

        // Despachar la acción para actualizar el estado global con los datos obtenidos
        this.setState({
          type: 'SET_FETCHED_DATA',
          payload: fetchedData,
        });

        // Opcional: agregar un mensaje al chatbot
        const message = this.createChatbotMessage('Datos cargados correctamente.');
        this.setState({
          type: 'ADD_MESSAGE',
          payload: message,
        });
  };
}

export default ActionProvider;
