import React, {useContext} from 'react';
import getPriceStock from '../Services'

import { DataContext } from '../App';

const ActionProvider = ({ createChatBotMessage, setState, children }) => {


  const { state, changeState } = useContext(DataContext);

  const handleSearchFinish = async ({state}) => {
    const botMessage = createChatBotMessage(
      "Listo",
      {
        loading: true,
        terminateLoading: true,
        withAvatar: true,
      }
    );
    addMessageToState(botMessage)
    const dt = await getPriceStock("")
    changeState((state) => ({
      ...state,
      priceStockList: dt.data
    }));

  };

  const handleInputStock = () => {
    const botMessage = createChatBotMessage(
      "Ingresa la clave de la accion a buscar:",
      {
        loading: true,
        terminateLoading: true,
        withAvatar: true,
      }
    );
    addMessageToState(botMessage)
  };

  const addMessageToState = (botMessage) => {
    setState((prev) => ({
      ...prev,
      messages: [...prev.messages, botMessage],
    }));
  };

  const addPriceStockList = ({ data }) => {
    setState((state) => ({
      ...state,
      priceStockList: data,
    }));
    localStorage.setItem('data', data);
  }

  // Put the handleHello and handleDog function in the actions object to pass to the MessageParser
  return (

    <div>
      {React.Children.map(children, (child) => {
        return React.cloneElement(child, {
          actions: {
            handleInputStock,
            handleSearchFinish,
          },
          utils: {
            state, changeState
          }
        });
      })}
    </div>
  );
};

export default ActionProvider;
