import React, { useState, useEffect, useContext } from "react";
import './Options.css'


import { DataContext } from '../App';

const StocksOptions = (props) => {

  const { state, changeState } = useContext(DataContext);
  const { setState, actionProvider } = props;

  const options = [
    {
      name: "AAPL",
      id: 1,
    },
    {
      name: "NVDA",
      id: 2,
    },
    {
      name: "MSFT",
      id: 3,
    },
  ];

  const setIDStock = (e) => {
    setState((state) => ({
      ...state,
      idStock: e.target.value,
    }));

    actionProvider.handleSearchFinish(props, state);
  };

  // return <Options options={options} {...props} />;

  return (
    <div className="options">
        {options.map(option => {
          return (
            <button
              value={option.name}
              className="option"
              onClick={setIDStock}
              key={option.id}
            >
              {option.name}
            </button>
          );
        })}
    </div>
  )
};

export default StocksOptions;
