import React from "react";
import { getListPriceStock, getPriceCurrent } from "../Services";

const ActionProvider = ({ createChatBotMessage, children, setState }) => {
  const handleHello = () => {
    const botMessage = createChatBotMessage("Hello. Nice to meet you.");

    setState((prev) => ({
      ...prev,
      messages: [...prev.messages, botMessage],
    }));
  };

  const handleSearchStockId = async (value, props) => {

    removeWidgetMessage();

    const message = createChatBotMessage(`Buscando ${value}...`, {
      loading: true,
      terminateLoading: true,
      withAvatar: true,
    });

    addMessageToState(message);

    const current = await getPriceCurrent(value);

    const { data } = await getListPriceStock();
    const ls = data.filter((item) => item.symbol == value);

    addListPriceStock(current, ls, count);
  };

  const addMessageToState = (message) => {
    setState((state) => ({
      ...state,
      messages: [...state.messages, message],
    }));
  };

  const removeWidgetMessage = () => {
    setState((state) => {
      const messages = state.messages.filter(message => {
        // Aquí filtramos los mensajes basados en el widgetId
        return (message.widget != 'Price' && message.type != 'bot');
      });
      return { ...state, messages };
    });
  };

  const addListPriceStock = (current, ls, count) => {
    setState((state) => ({
      ...state,
      currentPrice: current,
      listPriceStock: ls,
      count: count
    }));

    const message = createChatBotMessage(`Listo!`, {
      delay: 1000,
      loading: true,
      terminateLoading: true,
      withAvatar: true,
      widget: "Price",
    });

    addMessageToState(message);
  };

  return (
    <div>
      {React.Children.map(children, (child) => {
        return React.cloneElement(child, {
          actions: {
            handleHello,
            handleSearchStockId,
            removeWidgetMessage,
          },
        });
      })}
    </div>
  );
};

export default ActionProvider;
