import React, { useContext } from "react";

import "./App.css";
import Price from "../Componets/Price";
import ListHistory from "../Componets/ListHistory";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";

import Chatbot from "react-chatbot-kit";
import "./App.css";

import MessageParser from "../MessageParser";
import config from "../Config";

import { StateProvider } from "../StateContext.jsx";
import ActionProvider from '../ActionProvider';
import { StateContext } from '../StateContext.jsx';

const App = () => {

  const { dispatch } = useContext(StateContext);

  const actionProvider = new ActionProvider((message) => {
    dispatch({
      type: 'ADD_MESSAGE',
      payload: message,
    });
  }, dispatch);


  return (
    <StateProvider>
      <>
        <Container>
          {/* Stack the columns on mobile by making one full-width and the other half-width */}
          <Row>
            <Col xs={12} md={8}>
              <Row>
                <Price />
                <ListHistory />
              </Row>
            </Col>
            <Col xs={6} md={4}>
              <Chatbot
                config={config}
                actionProvider={actionProvider}
                messageParser={new MessageParser(actionProvider)}
              />
            </Col>
          </Row>
        </Container>
      </>
    </StateProvider>
  );
};

export default App;
